import React from 'react';
import ReactDOM from 'react-dom/client';
import {useState, useEffect} from "react";
import './index.css';
import {Products} from './Products'

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <div>
    <DisplayItems />
    <Counter/>
  </div>
);
function Counter() {
  const [counter, setCounter] = useState(0);
  const [message, setMessage] = useState("");
  useEffect(
    () => {console.log("Render")}, [message]);
  return (
  <div>
  <h1>Counter: {counter}</h1>
  <button onClick={()=>{setCounter(counter+1)}}>+</button>
  <button onClick={()=>setCounter(counter-1)}>-</button>
  
  </div>
  );
  }

function DisplayItems(){
  const [ProductsCategory, setProductsCategory] = useState(Products);
  const [query, setQuery] = useState('');
  const [toaster, setToaster] = useState(0);
  const [fan, setFan] = useState(0);
  const [eggs, setEggs] = useState(0);
  const [couch, setCouch] = useState(0);
  const [flowers, setFlowers] = useState(0);
  const [mug, setMug] = useState(0);


  const handleChange = (e) => {
    setQuery(e.target.value);
    const results = ProductsCategory.filter(eachProduct => {
   
    if (e.target.value === "") return ProductsCategory;
    return eachProduct.title.toLowerCase().includes(e.target.value.toLowerCase())
    });
    setProductsCategory(results);
    }

  return <div>
    <div class="search">
      <input type="search" value={query} onChange={handleChange} />
    </div>
    {ProductsCategory.map((product, index) => (
      <div key={index} >
        <img alt="Product Image" src={product.image} />
        <h3>{product.title}</h3>
        <p class="price">{product.price}</p>
        <p>{product.description}</p>
        <p>Add to Cart {product.cart}</p>
        if(index == 1)
        {
          //  <button onClick={()=>{setCounter(product.cart+1)}}>+</button>
          //  <button onClick={()=>setCounter(product.cart-1)}>-</button>
        }
        <button onClick={()=>{setToaster(toaster+1)}}>+</button>
        <button onClick={()=>setCounter(toaster-1)}>-</button>
      </div>
    ))}
  </div>
}
